miniplayer_shuffle = global.get('miniplayer_shuffle')
if (typeof miniplayer_shuffle == 'undefined'){
    miniplayer_shuffle = true  // Set to true as will toggle to false
}

if (miniplayer_shuffle){
    msg.params = ['false', msg.params[0]]
    global.set('miniplayer_shuffle', false)
}
else {
    msg.params = ['true', msg.params[0]]
    global.set('miniplayer_shuffle', true)
}
return msg;

if (msg["payload"]["devices"]) {
    for (var device, index = 0, devices = msg["payload"]["devices"], len = devices.length; index < len; index += 1) {
      device = devices[index];
      if (device["is_active"]) {
        return device;
      }
    }
}

var miniplayers = flow.get('miniplayers')
var spotify_miniplayers = flow.get('spotify_miniplayers')
var weather_miniplayers = flow.get('weather_miniplayers')
var mac_address = msg.topic.replace('/miniplayer/connection/', '')

if (typeof miniplayers == 'undefined'){
    miniplayers = []
}
if (typeof spotify_miniplayers == 'undefined'){
    spotify_miniplayers = []
}
if (typeof weather_miniplayers == 'undefined'){
    weather_miniplayers = []
}

if (msg.payload == 'connected'){
    if (!miniplayers.includes(mac_address)){
        miniplayers.push(mac_address) // Add mac_address to list
        flow.set('miniplayers', miniplayers)
        return {'payload': flow.get('miniplayers')}
    }
}

else if (msg.payload == 'disconnected'){
    if (miniplayers.includes(mac_address)){
        miniplayers.splice(miniplayers.indexOf(mac_address), 1)
        flow.set('miniplayers', miniplayers)
        if (spotify_miniplayers.includes(mac_address)){
            spotify_miniplayers.splice(spotify_miniplayers.indexOf(mac_address))
            flow.set('spotify_miniplayers', spotify_miniplayers)
            return {'payload': flow.get('miniplayers'), 'spotify': true}
        }
        if (weather_miniplayers.includes(mac_address)){
            weather_miniplayers.splice(weather_miniplayers.indexOf(mac_address))
            flow.set('weather_miniplayers', weather_miniplayers)
            return {'payload': flow.get('miniplayers'), 'weather': true}
        }
    }
}

